import {styled, keyframes} from 'styled-components';

const HomeContainer = styled.div`
width: 100%;
height: 3000px;
display: flex;
justify-content: center;
background-color: #EEE7DB;
font-size: 16px;
color: white;

`;


const typing = keyframes`
    from { width: 0 }
    to { width: 100% }
`;
const deleting = keyframes`
    from { width: 100% }
    to { width: 0 }
`;

export const NavBar = styled.nav`
padding: 20px;
height: 2vh;
display:flex;
width:100%;


`;

export const Name = styled.div`
    color:black;
    font-size: 1.4rem;
    animation: ${typing} 2s steps(20, end), ${deleting} 1s steps(10, end) 2s ;

`;

export const Name2 = styled.div`
color: #6C6C71;
font-size: 1.4rem;
overflow: hidden;
white-space: nowrap;
position: relative;
width: fit-content;

&:after {
  content: '';

  height: 100%;
  background-color: white;
  animation: ${typing} 2s steps(20, end) infinite, ${deleting} 1s steps(10, end) 2s infinite;
}
`;



export default HomeContainer;
