import React from 'react'
import './Image.css';
import suit from '../../Media/suit.jpeg'; // Ruta correcta a tu imagen
import { motion, useDragControls } from "framer-motion"


const Image = () => {
    const controls = useDragControls()

    function startDrag(event) {
        controls.start(event,{ snapToCursor: true })
      }


    return (
        <div>
            <div class="circular-container">
                <motion.div drag="x" dragControls={controls}
                  dragListener={false}
                > 

                </motion.div>


                {/* <div onPointerDown={startDrag} style={{ touchAction: "none" }}>lol</div> */}
                <img src={suit} alt="Imagen Circular" class="circular-image"/>
            </div>
        </div>
  )
}

export default Image
