import React from 'react'
import './Image.css';
import suit from '../../Media/suit.jpeg'; // Ruta correcta a tu imagen
import { motion, useDragControls } from "framer-motion"
import { useAnimationFrame } from "framer-motion"
import { useRef } from "react";


const Image = () => {
    const constraintsRef = useRef(null)


    return (
        <div>
            <div class="circular-container">
            <img src={suit} alt="Imagen Circular" class="circular-image"/>

            <motion.img
            src={suit}
            class="circular-image"
            alt="Imagen Circular"

            drag
            dragConstraints={{ top: 0, left: 0, right: 0, bottom: 0 }} // Restringe el movimiento dentro del contenedor
            />




            </div>
        </div>
  )
}

export default Image
