import React from 'react'
import './Image.css';
import suit from '../../Media/suit.jpeg'; // Ruta correcta a tu imagen
import { motion, useDragControls } from "framer-motion"
import { useAnimationFrame } from "framer-motion"
import { useRef } from "react";
import styled from 'styled-components';


const Image = () => {
    const constraintsRef = useRef(null)

    const ImgContainer = styled.div`
        width:76vw;
        height:30vw;
        margin-left:14vw;
        user-select:none;

    `;

    return (
        <ImgContainer ref={constraintsRef}>


            <motion.img className='item'
                src={suit}
                drag
                dragConstraints={constraintsRef} // Restringe el movimiento dentro de los límites de la pantalla
                dragElastic={0.1} // Agrega elasticidad al arrastre para un efecto suave
                dragTransition={{ bounceStiffness: 450, bounceDamping: 15 }} // Transición de arrastre con efecto de rebote
                initial={{ x: 100, y: 100 }} // Establece la posición inicial de la imagen
                animate={{ x: "-31vw", y: "-2vw" }} // Anima la imagen a la posición deseada

            />



        </ImgContainer>
  )
}

export default Image
