import {motion, transform} from "framer-motion"

const TextSpan = ({children}) => {
    transform = [
        "scale3D(1,1,1)",
        "scale3D(1,1,1)",
        "scale3D(1,1,1)",
        "scale3D(1,1,1)",
        "scale3D(1,1,1)",
        "scale3D(1,1,1)",
    ]




  return (
    <motion.span whileHover={{scale:1.2}}>
        {children}
    </motion.span>
  )
}

export default TextSpan
