import {styled, keyframes} from 'styled-components';

export const ScrollContainer = styled.div`
  height: 100vh;
  overflow-y: auto;
  scroll-snap-type: y mandatory;
  display: flex;
  flex-direction: column;
  position: relative; /* Añade posición relativa */
`;


export const ScrollCover = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  width: 12px; /* Ancho suficiente para cubrir la barra de desplazamiento */
  background-color: transparent; /* Fondo transparente */
  z-index: 999; /* Asegura que el cover esté en la parte superior */
`;

export const Section = styled.div`
  height: 100vh;
  scroll-snap-align: start;
  scroll-snap-type: y mandatory;
  scroll-behavior: smooth;
  background-color: #EEE7DB;

`;

export const Section3 = styled(Section)`
  scroll-snap-align: start;
`;
